<?php/* Plugin Name: Custom Bullet Lists Description: create custom bullet lists for WordPress Version: 1.0.1 Author: Gareth Gillman License: GPL2 Text-domain: cbl*/function cbl_tinymce_button() { global $typenow; if ( !current_user_can('edit_posts') && !current_user_can('edit_pages') ) {  return; } if( ! in_array( $typenow, array( 'post', 'page' ) ) )  return;  if ( get_user_option('rich_editing') == 'true') {   add_filter("mce_external_plugins", "cbl_tinymce_plugin");   add_filter('mce_buttons', 'cbl_register_btn');  }}add_action('admin_head', 'cbl_tinymce_button');function cbl_tinymce_plugin($plugin_array) { $plugin_array['cbl_btn'] = plugins_url( '/js/cbl-register-tinymce.js', __FILE__ ); return $plugin_array;}function cbl_register_btn($buttons) { array_push($buttons, "cbl_btn"); return $buttons;}// admin cssfunction cbl_admin_css() { wp_enqueue_style('cbl-admin-css', plugins_url('/cbl-styles.css', __FILE__));}add_action('admin_enqueue_scripts', 'cbl_admin_css');// frontend cssfunction cbl_css() { if ( !is_admin()) {  wp_enqueue_style('cbl-css', plugins_url('/cbl-styles.css', __FILE__)); }}add_action( 'wp_enqueue_scripts', 'cbl_css' );function cbl_editor_style( $mce_css ){ $mce_css .= ', ' . plugins_url( 'cbl-styles.css', __FILE__ ); return $mce_css;}add_filter( 'mce_css', 'cbl_editor_style' );